<script>
function checkBrowser(){
    c=navigator.userAgent.search("Chrome");
    f=navigator.userAgent.search("Firefox");
    m8=navigator.userAgent.search("MSIE 8.0");
    m9=navigator.userAgent.search("MSIE 9.0");
    if (c>-1){
        brwsr = "Chrome";
    }
    else if(f>-1){
        brwsr = "Firefox";
		$("div").hide();
		$("button").show();
		$("button").focus();
    }else if (m9>-1){
        brwsr ="MSIE 9.0";
    }else if (m8>-1){
        brwsr ="MSIE 8.0";
    }
    return brwsr;
}
function openWindow(url,name)
{
	window.open ('./tb_controller.php','Trotl Boben',"status=0,toolbar=0,location=0,menubar=0,scrollbars=0,directories=0,height=380,width=1050");
}
(checkBrowser());
</script>
